import axios from 'axios';
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setOthers } from '../redux/userSlice';
import { otheruser } from '../../../Backend/controllers/usercontroller';

const useGetOtherUsers = () => {
 useEffect(()=>{
const otherusers=async()=>{
    const {AuthenthecatedUser}=useSelector(store=>store.Users);
    const dispatch =useDispatch();
    if(!AuthenthecatedUser) return;
    axios.defaults.withCredentials;
    const res=await axios.get(`api/v1/user/${AuthenthecatedUser._id}`);
    dispatch(setOthers(res.data));

}
otherusers();
 }),[otheruser]};

export default useGetOtherUsers;
