import { useState } from 'react';
import './style.css';

const Login = () => {
  const [signUp,setsignUp]=useState(true);
  const [user,setuser]=useState({
    Fullname:"",
    userId:"",
    password:"",
    coinfirmPass:"",
    Gender:""
  })
  const handlesignUp=()=>{
    if(signUp===true){
    setsignUp(false);
    }
    else{
      setsignUp(true);
    }
  }
  const handleGenderChange = (e) => {
    setuser({ ...user, gender: e.target.value });
  }
  const submitHandler=(e)=>{
    e.preventDefault();
    console.log(user);
  }
  return (
    <div><form onSubmit={submitHandler}> 
        <div className="container">
          <h1>{!signUp?"Log In":"Sign In"}</h1>{signUp && 
          <div className="input-container"><span>Full name </span><input type="text" value={user.Fullname} placeholder='your name...' onChange={(e)=>setuser({...user,Fullname:e.target.value})}/></div>}
          <div className="input-container"> <span>User name</span> <input type="text" value={user.userId} placeholder='User id' onChange={(e)=>setuser({...user,userId:e.target.value})} /></div>
          <div className="input-container"> <span>Password</span> <input type="password" value={user.password} placeholder='password' onChange={(e)=>setuser({...user,password:e.target.value})} /></div>{signUp &&
         <> <div className="input-container"> <span>Confirm Password</span><input type="password" value={user.coinfirmPass} placeholder='Confirm Password' onChange={(e)=>setuser({...user,coinfirmPass:e.target.value})}/></div>
          <span className='gender'>Gender </span>
          <div className="radio-btn">
                <input type="radio" id="male" value="male" checked={user.gender === "male"} onChange={handleGenderChange} />
                <label htmlFor="male">Male</label>
                <input type="radio" id="female" value="female" checked={user.gender === "female"} onChange={handleGenderChange} />
                <label htmlFor="female">Female</label>
              </div></>}
          <div className="text"><br />
            <a href="http://goooglr.com"><p >Forget password ? </p></a>
            <p onClick={handlesignUp}> { !signUp ?"Don't have account ? Create new account ":"Alredy have Account ? Log In"}</p>
          </div>
          <div className="btn"><button>{!signUp?"Log In":"Sign In"}</button> </div>
        </div>
        </form></div>
  )
}

export default Login;
